                    </ButtonGroup>
                    <ButtonGroup className="mr-2" aria-label="Third group">
                        <Button>8</Button>
                    </ButtonGroup>
                    <ButtonGroup className="mr-2" aria-label="Third group">
                        <Button>8</Button>
                    </ButtonGroup>
                    <Dropdown.Item eventKey="2">Dropdown link</Dropdown.Item>
                </DropdownButton>
                </Col>  
                <Col sm={4}>
                    <Button onClick={enableEraser}>ERASER</Button>
                </Col>  

            </Row>
        </Container>

        </>

    );
}

export default CanvasPage;
